  userPermission.classList.add("user-table");

  // Create the table element
  const table = document.createElement("table");

  // Create the table header and header row
  const thead = document.createElement("thead");
  columnTable.forEach((columnTable) => {
    // Add a column header for "name"
    const headerRow = document.createElement("tr");
    const th = document.createElement("th");
    th.textContent = columnTable;
    headerRow.appendChild(th);
    thead.appendChild(headerRow);
  });

  // Create the table body
  const tbody = document.createElement("tbody");

  // Add rows for each user in the users array
  users.forEach((user) => {
    const row = document.createElement("tr");
    const td = document.createElement("td");
    td.textContent = user;
    row.appendChild(td);
    tbody.appendChild(row);

    // Create and append the checkbox cell
    const tdCheckbox = document.createElement("td");
    const checkbox = document.createElement("input");
    checkbox.type = "checkbox";
    checkbox.name = "userSelect";
    checkbox.value = user;
    tdCheckbox.appendChild(checkbox);
    row.appendChild(tdCheckbox);

    tbody.appendChild(row);
  });

  // Append the table elements to the userPermission div
  table.appendChild(thead);
  table.appendChild(tbody);
  userPermission.appendChild(table);

